document.addEventListener('DOMContentLoaded', function () {
  AOS.init({ startEvent: 'load', duration: 500 });
  let headerBurger = document.querySelector('.header__burger');
  let headerNav = document.querySelector('.header__nav');
  let headerMain = document.querySelector('.header__main');
  let headerContacts = document.querySelector('.header__contacts');
  headerBurger.addEventListener('click', function () {
    headerBurger.classList.toggle('active'),
      headerNav.classList.toggle('active'),
      headerContacts.classList.toggle('active'),
      headerMain.classList.toggle('active'),
      product.classList.toggle('active');
  });
  new Swiper('.slider1', {
    direction: 'horizontal',
    loop: !0,
    spaceBetween: 30,
    slidesPerView: 4,
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
    },
    breakpoints: {
      300: { slidesPerView: 1, spaceBetween: 10 },
      540: { slidesPerView: 2, spaceBetween: 20 },
      769: { slidesPerView: 3, spaceBetween: 20 },
      900: { slidesPerView: 4, spaceBetween: 30 },
    },
  }),
    new Swiper('.slider2', {
      direction: 'horizontal',
      loop: !0,
      spaceBetween: 30,
      breakpoints: { 700: { slidesPerView: 1, spaceBetween: 20 } },
    });
  document.querySelector('.phone').addEventListener('keydown', function (e) {
    'ArrowLeft' != e.key &&
      'ArrowRight' != e.key &&
      'Backspace' != e.key &&
      'Tab' != e.key &&
      e.preventDefault();
    var t = '+7 (111) 111-11-11';
    if (/[0-9\+\ \-\(\)]/.test(e.key)) {
      var i = this.value,
        r = i.length;
      if (/[0-9]/.test(e.key))
        if ('1' == t[r]) this.value = i + e.key;
        else
          for (var a = r; a < t.length; a++) {
            if ('1' == t[a]) {
              this.value = i + e.key;
              break;
            }
            i += t[a];
          }
    }
  });
});
